    <script>
        function quickView(product_id, type) {
            $('#pre-loader').show();
            let payloadData = {
                _token: $('meta[name="csrf-token"]').attr('content'),
                product_id: product_id,
                type: type
            };
            $.post('item-details-for-get-modal.html', payloadData, function(data) {
                $(".add-product-to-cart-using-modal").html(data);
                $("#theme_modal").modal('show');
                $('.nc_select, .select_address, #product_short_list, #paginate_by').niceSelect();
                $('#pre-loader').hide();
            });
        }

        function addToCart(product_sku_id, seller_id, qty, price, shipping_type, type, prod_info = null, giftCardType =
            null, auction_type = null) {
            $('#add_to_cart_btn').prop('disabled', true);
            $('#add_to_cart_btn').html("Adding");
            var formData = new FormData();
            formData.append('_token', $('meta[name="csrf-token"]').attr('content'));
            formData.append('price', price);
            formData.append('qty', qty);
            formData.append('product_id', product_sku_id);
            formData.append('seller_id', seller_id);
            formData.append('shipping_method_id', shipping_type);
            formData.append('type', type);
            formData.append('is_buy_now', 'no');
            formData.append('gift_card_type', giftCardType);
            formData.append('auction_type', auction_type);
            $('#pre-loader').show();
            var base_url = $('#url').val();
            $.ajax({
                url: base_url + "/cart/store",
                type: "POST",
                cache: false,
                contentType: false,
                processData: false,
                data: formData,
                success: function(response) {
                    if (response.cart_details_submenu == 'out_of_stock') {
                        toastr.error('No more product to buy.');
                        $('#pre-loader').hide();
                        $('#add_to_cart_btn').prop('disabled', false);
                        $('#add_to_cart_btn').html("Add to Cart");
                    } else {
                        $('#add_to_cart_btn').prop('disabled', false);
                        $('#add_to_cart_btn').html("Add to Cart");
                        if (prod_info != null) {
                            $('#cart_suceess_thumbnail').attr('src', prod_info.thumbnail);
                            $('#cart_suceess_thumbnail').attr('alt', prod_info.name);
                            $('#cart_suceess_thumbnail').attr('title', prod_info.name);
                            $('#cart_suceess_name').text(prod_info.name);
                            $('#cart_suceess_price').text(numbertrans(prod_info.price));
                            $('#cart_suceess_url').attr('href', prod_info.url);
                            $('#cart_add_modal').modal('show');
                        }
                        $('#cart_data_show_div').html(response.cart_details_submenu);
                        $('.shoping_cart').addClass('active');
                        $('.cart_count_bottom').text(numbertrans(response.count_bottom));
                        if ($(".add-product-to-cart-using-modal").length) {
                            $('.add_to_cart_modal').modal('hide');
                        }
                        $('#pre-loader').hide();
                    }
                },
                error: function(response) {
                    toastr.error("Product not added", "Error");
                    $('#add_to_cart_btn').prop('disabled', false);
                    $('#add_to_cart_btn').html("Add to Cart");
                    $('#pre-loader').hide();
                }
            });
        }

        function cartProductDelete(id, p_id, btn_id) {
            $('#pre-loader').show();
            $(btn_id).prop("disabled", true);
            var formData = new FormData();
            formData.append('_token', $('meta[name="csrf-token"]').attr('content'));
            formData.append('id', id);
            formData.append('p_id', p_id);
            var base_url = $('#url').val();
            $.ajax({
                url: base_url + "/cart/delete",
                type: "POST",
                cache: false,
                contentType: false,
                processData: false,
                data: formData,
                success: function(response) {
                    toastr.success("Product successfully deleted from cart", "Success");
                    $('#cart_details_div').html(response.MainCart);
                    $(btn_id).prop("disabled", false);
                    $('#cart_data_show_div').html(response.SubmenuCart);
                    $('.shoping_cart').addClass('active');
                    $('.cart_count_bottom').text(numbertrans(response.count_bottom));
                    $('#pre-loader').hide();
                },
                error: function(response) {
                    $(btn_id).prop("disabled", false);
                    $('#pre-loader').hide();
                }
            });
        }

        function updateCartQuantity(cartId, newQuantity) {
            if (newQuantity < 1) {
                cartProductDelete(cartId, null, '#update_btn_' + cartId);
                return;
            }

            $('#pre-loader').show();
            var formData = new FormData();
            formData.append('_token', $('meta[name="csrf-token"]').attr('content'));
            formData.append('cart_id', cartId);
            formData.append('quantity', newQuantity);
            var base_url = $('#url').val();
            $.ajax({
                url: base_url + "/cart/update",
                type: "PUT",
                cache: false,
                contentType: false,
                processData: false,
                data: formData,
                success: function(response) {
                    if (response.success) {
                        toastr.success("Cart updated successfully", "Success");
                        // Reload the cart page to show updated quantities
                        location.reload();
                    } else {
                        toastr.error(response.message || "Failed to update cart", "Error");
                    }
                    $('#pre-loader').hide();
                },
                error: function(response) {
                    toastr.error("Failed to update cart", "Error");
                    $('#pre-loader').hide();
                }
            });
        }

        function deleteAlItem() {
            $('#delete_all_btn').prop("disabled", true);
            $('#pre-loader').show();
            var base_url = $('#url').val();
            var formData = new FormData();
            formData.append('_token', $('meta[name="csrf-token"]').attr('content'));
            $.ajax({
                url: base_url + "/cart/delete-all",
                type: "POST",
                cache: false,
                contentType: false,
                processData: false,
                data: formData,
                success: function(response) {
                    toastr.success("Product successfully deleted from cart", "Success");
                    $('#cart_details_div').empty();
                    $('#cart_details_div').html(response.MainCart);
                    $('#delete_all_btn').prop("disabled", false);
                    $('#cart_inner').empty();
                    $('#cart_inner').html(response.SubmenuCart);
                    $('#pre-loader').hide();
                },
                error: function(response) {
                    $('#delete_all_btn').prop("disabled", false);
                    $('#pre-loader').hide();
                }
            });
        }

        function addToWishlist(product_id, seller_id, type) {
            // Check if user is logged in
            let is_login = $('#login_check').val();
            if (is_login != 1) {
                toastr.warning("Please login first", "Warning");
                return;
            }

            $('#wishlist_btn').addClass('wishlist_disabled');
            $('#wishlist_btn').html("Adding");
            $('#pre-loader').show();

            $.post("{{ route('wishlist.store') }}", {
                _token: $('meta[name="csrf-token"]').attr('content'),
                product_id: product_id
            }, function(data) {
                if (data.result == 1) {
                    toastr.success(data.message, "Success");
                    $('#wishlist_btn').removeClass('wishlist_disabled');
                    $('#wishlist_btn').html("Add to wishlist");
                    $('.wishlist_count').text(data.totalItems);
                } else if (data.result == 3) {
                    toastr.warning(data.message, "Info");
                    $('#wishlist_btn').removeClass('wishlist_disabled');
                    $('#wishlist_btn').html("Add to wishlist");
                    $('.wishlist_count').text(data.totalItems);
                } else {
                    toastr.error("Something went wrong", "Error");
                    $('#wishlist_btn').removeClass('wishlist_disabled');
                    $('#wishlist_btn').html("Add to wishlist");
                }
                $('#pre-loader').hide();
            }).fail(function() {
                toastr.error("Error adding to wishlist", "Error");
                $('#wishlist_btn').removeClass('wishlist_disabled');
                $('#wishlist_btn').html("Add to wishlist");
                $('#pre-loader').hide();
            });
        }

        // Remove from wishlist
        function removeFromWishlist(wishlist_id) {
            if (!wishlist_id) return;

            $('#pre-loader').show();

            $.ajax({
                url: "{{ url('/wishlist') }}/" + wishlist_id,
                type: 'DELETE',
                data: {
                    _token: $('meta[name="csrf-token"]').attr('content')
                },
                success: function(data) {
                    if (data.result == 1) {
                        toastr.success(data.message, "Success");
                        $('.wishlist_count').text(data.totalItems);
                        // Reload page if on wishlist page
                        if (window.location.pathname === '/wishlist') {
                            location.reload();
                        }
                    } else {
                        toastr.error(data.message, "Error");
                    }
                    $('#pre-loader').hide();
                },
                error: function() {
                    toastr.error("Error removing from wishlist", "Error");
                    $('#pre-loader').hide();
                }
            });
        }

        // Toggle wishlist (add/remove)
        function toggleWishlist(product_id) {
            // Check if user is logged in
            let is_login = $('#login_check').val();
            if (is_login != 1) {
                toastr.warning("Please login first", "Warning");
                return;
            }

            $('#pre-loader').show();

            $.post("{{ route('wishlist.toggle') }}", {
                _token: $('meta[name="csrf-token"]').attr('content'),
                product_id: product_id
            }, function(data) {
                if (data.result == 1 || data.result == 2) {
                    toastr.success(data.message, "Success");
                    $('.wishlist_count').text(data.totalItems);

                    // Update wishlist button appearance
                    let wishlistBtn = $('#wishlistbtn_' + product_id);
                    if (data.action === 'added') {
                        wishlistBtn.addClass('is_wishlist');
                    } else {
                        wishlistBtn.removeClass('is_wishlist');
                    }
                } else {
                    toastr.error("Something went wrong", "Error");
                }
                $('#pre-loader').hide();
            }).fail(function() {
                toastr.error("Error updating wishlist", "Error");
                $('#pre-loader').hide();
            });
        }

        function wishlistToggle(id) {
